import "./App.css";
import { React, useEffect, useState } from "react";

async function sleep(delay) {
    return new Promise((res) => setTimeout(res, delay));
}

function App() {
    var seg = 0;
    var min = 0;
    var hora = 0;
    const [tempo, setTempo] = useState("");

    useEffect(() => {
        seg += 1;
        sleep(1000);
        if (seg == 60) {
            min += 1;
            seg = 0;
        }
        if (min == 60) {
            hora += 1;
            min = 0;
        }
        tempo = hora + ":" + min + ":" + seg;
    });

    return (
        <div className="App">
            <h1>TESTE</h1>
        </div>
    );
}

export default App;
